import React from 'react';

//functional component and stateful component
const Square = (props) => {
     
    return <h1 style={{color:props.color, fontWeight:'bold'}}>{props.text}</h1>;
    // note the double curly braces here:
    // the style property needs to be a complete javascript object,
    // and since we are embedding this value, it is also being wrapped in a set of
    // curly braces for JSX
}
 
const App = (props) => {
    return (
        <div>
            <Square text="white on blue" color="white" />
            <Square text="blue on red" color="blue" />
            <Square text="green on pink" color="green" />
        </div>
    )
}


export default App;
